mapper.select:

select 

from DTC_TABLE_WHITE_LIST
where SOURCE_ID = #{sourceId,jdbcType=VARCHAR}
  and TABLE_NAME = #{tableName,jdbcType=VARCHAR}

mapper.delete:

delete from DTC_TABLE_WHITE_LIST
where SOURCE_ID = #{sourceId,jdbcType=VARCHAR}
  and TABLE_NAME = #{tableName,jdbcType=VARCHAR}

mapper.update:

update DTC_TABLE_WHITE_LIST
set SOURCE_NAME = #{sourceName,jdbcType=VARCHAR},
  SYNC_NUMS_PER_PAGE = #{syncNumsPerPage,jdbcType=DECIMAL},
  IS_USE_PARAMS = #{isUseParams,jdbcType=CHAR},
  PARAMS = #{params,jdbcType=VARCHAR},
  SYNC_SEQ = #{syncSeq,jdbcType=DECIMAL},
  TRG_TABLE_NAME = #{trgTableName,jdbcType=VARCHAR}
where SOURCE_ID = #{sourceId,jdbcType=VARCHAR}
  and TABLE_NAME = #{tableName,jdbcType=VARCHAR}
